    private void lblNamaCaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_lblNamaCaretUpdate
        // TODO add your handling code here:
        if(lblTelp.getText().length()==0||lblBB.getText().length()==0||lblNama.getText().length()==0||"0".equals(lblBB.getText())){
            jbProses.setEnabled(false);
        }else{
            jbProses.setEnabled(true);
        }
    }//GEN-LAST:event_lblNamaCaretUpdate

