    private void jbProsesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbProsesActionPerformed
        // TODO add your handling code here:
        double BB,diskon,total,KG=11500,totalD;
        BB=Double.parseDouble(lblBB.getText());
        diskon=SliderDiskon.getValue();
            
        if(rbExpress.isSelected()){
            total=BB*(KG*1.5);
            totalD=total-(total*(diskon/100));
            lblTotalHarga.setText(String.valueOf(totalD));
        }else if(rbReguler.isSelected()){
            total=BB*KG;
            totalD=total-(total*(diskon/100));
            lblTotalHarga.setText(String.valueOf(totalD));
        }
            
    }//GEN-LAST:event_jbProsesActionPerformed

